<style lang="less" scoped>
.calcBox {
    width: 335px;
    height: 150px;
    // border: solid red 1px;
    position: fixed;
    right: 0;
    bottom: 30px;
    z-index:300;
    display:grid;
    grid-template-columns: 30px 1fr 20px 1fr;
    >div {
        height: 100%;
        background:#3A6366;
    }
    transition: 0.3s;
}
.leftBtn {
    background: #102452 !important;height:100%;
    border-right: solid #F5F7F9 5px;
    cursor: pointer;
}
.twoBox {
    padding:13%;
    select{
        width: 100%;
        margin-bottom: 1em;
        padding-left:5%;
    }
    .twoBoxItem {
        height: 70%;
        background: #fff;
        font-size: 30px;
        position: relative;
        overflow:hidden;
        input {
            position: absolute;
            width:118%;
            height:100%;
            left:0;
            top: 0;
            text-align:center;
            outline: none;
            border: none;
        }
    }
}
.twoBoxCenter {
  color: #FFF;
  font-size:50px;
  flex-direction: column;
  padding-top: 75px;
  &::after {
      width: 8px;
      height: 8px;
      border-radius: 50%;
      background: #FFF;
      display: block;
      content: "";
      margin: 5px auto;
  }
  &::before {
      width: 8px;
      height: 8px;
      border-radius: 50%;
      background: #FFF;
      display: block;
      content: "";
      margin: 5px auto;
  }
}
.hideBox {
    width: 30px !important;
}
.SliderLeft {
    box-shadow: 0 0 15px #666;
    position: relative;
    z-index: 100;
    background:rgba(255,255,255,0.8);
}
</style>

