    <Layout>
      <!-- <Header class="header-con">
        <header-bar :collapsed="collapsed" @on-coll-change="handleCollapsedChange">
          <user :message-unread-count="unreadCount" :user-avator="userAvator"/>
          <language v-if="$config.useI18n" @on-lang-change="setLocal" style="margin-right: 10px;" :lang="local"/>
          <fullscreen v-model="isFullscreen" style="margin-right: 10px;"/>
        </header-bar>
      </Header> -->
      <div class="my-Header sb">
            <div></div>
            <div class="ju al" >
                <language v-if="$config.useI18n" @on-lang-change="setLocal" style="margin-right: 10px;" :lang="local"/>
                <user :message-unread-count="unreadCount" :user-avator="userAvator"/>
            </div>
        </div>
      <Content class="main-content-con">
        <Layout class="main-layout-con">
          <div class="tag-nav-wrapper">
            <tags-nav :value="$route" @input="handleClick" :list="tagNavList" @on-close="handleCloseTag"/>
          </div>
          <Content class="content-wrapper">
            <!-- <keep-alive :include="cacheList"> -->
            <keep-alive>
              <router-view/>
            </keep-alive>
            <!-- <ABackTop :height="100" :bottom="80" :right="50" container=".content-wrapper"></ABackTop> -->
          </Content>
        </Layout>
      </Content>
    </Layout>
    <div :class="['calcBox', { hideBox: show }]">
        <div class="leftBtn op al" @click="show = !show">
            <Icon type="md-arrow-dropleft" size="25" color="#FFF" v-if="show" />
            <Icon type="md-arrow-dropright" size="25" color="#FFF" v-else />
        </div>
        <div class="twoBox">
            <select v-model="unit1">
                <option v-for="(item,i) in leftList" :key="i">{{item.name}}</option>
            </select>
            <div class="twoBoxItem ju al">
                <input type="number" v-model="val1" @input="enter1" />
            </div>
        </div>
        <div class="twoBoxCenter"></div>
        <div class="twoBox">
            <select v-model="unit2">
                <option v-for="(item,i) in rightList" :key="i">{{item.name}}</option>
            </select>
            <div class="twoBoxItem ju al">
                <input type="number" v-model="val2" @input="enter2" />
            </div>
        </div>
    </div>
    <Fix></Fix>
  </Layout>
</template>
<script>
import SideMenu from './components/side-menu'
import HeaderBar from './components/header-bar'
import TagsNav from './components/tags-nav'
import User from './components/user'
import ABackTop from './components/a-back-top'
import Fullscreen from './components/fullscreen'
import Language from './components/language'
import ErrorStore from './components/error-store'
import { mapMutations, mapActions, mapGetters } from 'vuex'
import { getNewTagList, routeEqual } from '@/libs/util'
import routers from '@/router/routers'
import minLogo from '@/assets/images/logo-min.png'
import maxLogo from '@/assets/images/login.png'
import './main.less'
export default {
  name: 'Main',
  components: {
    SideMenu,
    HeaderBar,
    Language,
    TagsNav,
    Fullscreen,
    ErrorStore,
    User,
    ABackTop
  },
  data () {
    return {
      collapsed: false,
      minLogo,
      maxLogo,
      isFullscreen: false,
      show: true,
      val1: "",
      val2: "",
      unit1: "千克",
      unit2: "噸",
        leftList: [
            { name: "千克" }
        ],
        rightList: [
            { name: "噸" }
        ],
        allList: [
            // [
            //    { name: "升" },
            // ],
            // [
            //     { name: "" }
            // ]
        ]
    }
  },
  
  computed: {
    ...mapGetters([
      'errorCount'
    ]),
    tagNavList () {
      return this.$store.state.app.tagNavList
    },
    tagRouter () {
      return this.$store.state.app.tagRouter
    },
    userAvator () {
      // 退出登录图片暂时默认
      // return this.$store.state.user.avatorImgPath
      return "https://file.iviewui.com/dist/a0e88e83800f138b94d2414621bd9704.png"
    },
    cacheList () {
      const list = ['ParentView', ...this.tagNavList.length ? this.tagNavList.filter(item => !(item.meta && item.meta.notCache)).map(item => item.name) : []]
      return list
    },
    menuList () {
      return this.$store.getters.menuList
    },
    local () {
      return this.$store.state.app.local
    },
    hasReadErrorPage () {
      return this.$store.state.app.hasReadErrorPage
    },
    unreadCount () {
      return this.$store.state.user.unreadCount
    }
  },
  methods: {
    ...mapMutations([
      'setBreadCrumb',
      'setTagNavList',
      'addTag',
      'setLocal',
      'setHomeRoute',
      'closeTag'
    ]),
    ...mapActions([
      'handleLogin',
      'getUnreadMessageCount'
    ]),
    enter1 () {
        this.val2 = Number(this.val1) / 1000
    },
    enter2 () {
        this.val1 = Number(this.val2) * 1000
    },
    turnToPage (route) {
      let { name, params, query } = {}
      if (typeof route === 'string') name = route
      else {
        name = route.name
        params = route.params
        query = route.query
      }
      if (name.indexOf('isTurnByHref_') > -1) {
        window.open(name.split('_')[1])
        return
      }
      this.$router.push({
        name,
        params,
        query
      })
    },
    handleCollapsedChange (state) {
      this.collapsed = state
    },
    handleCloseTag (res, type, route) {
      if (type !== 'others') {
        if (type === 'all') {
          this.turnToPage(this.$config.homeName)
        } else {
          if (routeEqual(this.$route, route)) {
            this.closeTag(route)
          }
        }
      }
      this.setTagNavList(res)
    },
    handleClick (item) {
      this.turnToPage(item)
    }
  },
  watch: {
    '$route' (newRoute) {
      // console.log(newRoute)
      const { name, query, params, meta } = newRoute
      this.addTag({
        route: { name, query, params, meta },
        type: 'push'
      })
      // this.setBreadCrumb(newRoute)  //面包屑报错 01 07
      this.setTagNavList(getNewTagList(this.tagNavList, newRoute))
      this.$refs.sideMenu.updateOpenName(newRoute.name)
    }
  },
  mounted () {
    /**
     * @description 初始化设置面包屑导航和标签导航
     */
    this.setTagNavList()
    this.setHomeRoute(routers)
    const { name, params, query, meta } = this.$route
    this.addTag({
      route: { name, params, query, meta }
    })
    // this.setBreadCrumb(this.$route)   //面包屑报错 01 07
    // 设置初始语言
    this.setLocal(this.$i18n.locale)
    // 如果当前打开页面不在标签栏中，跳到homeName页
    if (!this.tagNavList.find(item => item.name === this.$route.name)) {
      this.$router.push({
        name: this.$config.homeName
      })
    }
    // 获取未读消息条数
    this.getUnreadMessageCount()
  }
}
</script>

